import "./ImageView.css";
//  if we keep the images in the public folder ! : no need for any path traversal '../../' like this
import defaultPathDefaultImg from "../public/default-no-img.png";

export default function ImageView ( { src, alt, show } )
{
    return ( 
        <div className="container">
            <img src={show ? src : defaultPathDefaultImg} alt={alt}/>
        </div>
    );
}
