import OnChat from "./OnChat";

export default function ChatLog ( {messages, setMessages, id, setId} )
{
    return ( 
        <>
            <h1>
                the chat log...
            </h1>
            <ul>
                { messages.map ( msg => <><OnChat message={msg} setMessage={setMessages} id={setId ( id ++ ) }/></>) } 
            </ul>
        </>
    )
}
