  return (
      <Routes>
        <Route path='/' element={<Join navigate={navigate} setUser={setUsername}/>}/> 
        <Route path="/chat" element={<ChatRoom username={username} navigate={navigate} setMessages={setMessages}/>}/>
      </Routes>
  );
}
